namespace QuarterMark.Api.Infrastructure.Data;

public static class ImagePool
{
    // Hardcoded list of image URLs - replace these with your actual image URLs
    // You can use any image hosting service (Imgur, Cloudinary, your own server, etc.)
    // Each image will only be shown once per round
    private static readonly List<string> _images = new()
    {
        "data:image/jpeg;base64,",
        "data:image/jpeg;base64,",
       
    };

    public static List<string> GetAllImages()
    {
        return new List<string>(_images);
    }

    public static string? GetRandomUnusedImage(List<string> usedImages)
    {
        var availableImages = _images.Except(usedImages).ToList();
        if (availableImages.Count == 0)
            return null;

        var random = new Random();
        var index = random.Next(availableImages.Count);
        return availableImages[index];
    }
}

